import Vue from 'vue';
import VueRouter from 'vue-router';

Vue.use(VueRouter);

// 解决导航栏或者底部导航tabBar中的vue-router在3.0版本以上频繁点击菜单报错的问题。
const originalPush = VueRouter.prototype.push;
VueRouter.prototype.push = function push(location) {
  return originalPush.call(this, location).catch(err => err);
};

const routes = [
  {
    path: '/',
    name: 'Manager',
    component: () => import('../views/Manager.vue'),
    children: [
      { path: '403', name: 'NoAuth', meta: { name: '无权限' }, component: () => import('../views/manager/403') },
      { path: 'home', name: 'Home', meta: { name: '系统首页' }, component: () => import('../views/manager/Home') },
      // 其他路由配置...
    ]
  },
  {
    path: '/front',
    name: 'Front',
    component: () => import('../views/Front.vue'),
    children: [
      { path: 'home', name: 'FrontHome', meta: { name: '用户首页', role: 'user' }, component: () => import('../views/front/Home.vue') },
      // 其他用户路由...
    ]
  },
  {
    path: '/merchant',
    name: 'Merchant',
    component: () => import('../views/Merchant.vue'),
    children: [
      { path: 'home', name: 'MerchantHome', meta: { name: '商家首页', role: 'merchant' }, component: () => import('../views/merchant/Home.vue') },
      // 其他商家路由...
    ]
  },
  // 其他路由...
];

const router = new VueRouter({
  routes
});

// 添加导航守卫
router.beforeEach((to, from, next) => {
  const userRole = localStorage.getItem('userRole'); // 假设用户角色存储在 localStorage 中

  if (userRole === 'user' && to.meta.role !== 'user') {
    return next('/front/home');
  }
  if (userRole === 'merchant' && to.meta.role !== 'merchant') {
    return next('/merchant/home');
  }

  next();
});

export default router;
